import './Audio.css';
let changeRewind = function(arg) {
    let rewind_increment = (arg > 0)? 0.5 : -0.5;
    if(rewind==0 && arg == 0) return;
    else rewind = rewind + rewind_increment;
    document.getElementById("rewind").innerText = rewind * 1000;
}
function Audio() {
    return <>
        <input id="audio_file" type="file" accept="audio/*" />
        <hr />
        <button className="rewind-controller" id="rewind-down" onClick={changeRewind(0)}> - </button>
            <span>Auto-rewind : </span><span id="rewind">1000</span><span>ms</span>
        <button className="rewind-controller" id="rewind-up" onClick={changeRewind(1)}> + </button>
        <audio controls id="audio_player"></audio>
        <hr />
    </>;
}
export default Audio;
