import './Text.css';
const textStyle = {
    height: '65vh',
    fontSize: '30px',
    textAlign: 'left'
}
function Text() {
    return <div>
        <button id="dir-toggle" onClick={toggleDir()}>rtl</button>
        <button className="font-size-controller" id="font-size-down" onClick="changeFontSize(0)"> - </button>
        <span>Size : </span><span id="font-size">30px</span>
        <button className="font-size-controller" id="font-size-up" onClick="changeFontSize(1)"> + </button>
        <button id="save" onClick={saveWork()}>Save [ ]</button>
        <button id="reset-settings" onClick="resetSettings()">Reset Settings</button><br />
        <textarea dir="ltr" id="text" style={textStyle}></textarea>
    </div>;
}
export default Text;
